@media screen and (max-width: 728px) {

}

/*TAMANHO SELOS RODAPÉ*/
.stamps img {
    max-height: 30px;
}

/*ATUALIZAÇÃO LAYOUT*/

/*COR CABEÇALHO*/
.header {
    background: #000000;
}

/*COR ÍCONES CABEÇALHO*/
.header__buttons .btn {
    color: #FFFFFF;
}

/*COR ÍCONES CABEÇALHO AO PASSAR O MOUSE*/
.header__buttons .btn:hover {
    color: #5ABA47;
}

/*COR TEXTO MENU*/
.header__nav--full a {
    color: #ffffff;
}

/*ESPAÇAMENTO MENU PARA A LOGO*/
.header__nav {
    padding-top: 12px;
}

/*COR FUNDO MENU CATEGORIAS AO PASSAR MOUSE*/
.header__nav a:hover {
    background-color: green;
}

/*COR ICONE ABERTURA ICONE MENU LATERAL*/
.fa-bars:before {
    content: "\e818";
    color: #ffffff;
}

@media screen and (min-width: 728px) {
    /*RETIRAR BARRAS DE INFORMAÇÃO*/
  .d-md-block {
    display: none;
  }   
}
