body{
  font-family: 'Aller', 'Noto Sans JP', sans-serif;
}
/*TAMANHO LOGO*/
@media (min-width: 576px){
  .header__logo {
    max-width: 250px;
   }
}

/*COR CABEÇALHO*/
.header {
    background: #000000;
    border-color: #5aba47;
    border-width: 3px;
}

/*COR ÍCONES CABEÇALHO*/
.header__buttons .btn {
    color: #FFFFFF;
}

/*COR ÍCONES CABEÇALHO AO PASSAR O MOUSE*/
.header__buttons .btn:hover {
    color: #5ABA47;
}

/*COR TEXTO MENU*/
.header__nav--full a {
    color: #ffffff;
}

/*ESPAÇAMENTO MENU PARA A LOGO*/
.header__nav {
    padding-top: 12px;
}

/*COR FUNDO MENU CATEGORIAS AO PASSAR MOUSE*/
.header__nav a:hover {
    background-color: green;
}

/*COR ICONE ABERTURA ICONE MENU LATERAL*/
.fa-bars:before {
    content: "\e818";
    color: #ffffff;
}

  /*RETIRAR BARRA INFORMAÇÕES HEADER*/
@media (min-width: 768px){
    .top-bar__nav {
      display: none!important;
    }
}

/*COR HEADER CARRINHO*/
.app__header {
    background: black;
}

/*COR TEXTO HEADER CARRINHO*/
.app__contact span:not(:first-child) {
    color: white;
}

/*COR TEXTO "AMBIENTE SEGURO" HEADER CARRINHO*/
.app__safe {
    display: none;
    color: white;
}

/*BOTÃO COMPRAR FIXO*/
.product-card__buy.fade {
    opacity: 1!important;
    padding-top: 10px;
    -webkit-transform: skew(-20deg);
       -moz-transform: skew(-20deg);
         -o-transform: skew(-20deg);
            transform: skew(-20deg);
    text-transform: uppercase;
    color: green;
}

/*INCLINAÇÃO BOTÃO COMPRAR*/
.product__buy {
    -webkit-transform: skew(-20deg);
    -moz-transform: skew(-20deg);
    -o-transform: skew(-20deg);
    transform: skew(-20deg);
    padding-left: 7px;
}

/*DISTANCIAMENTO PREÇO VELHO*/
.prices__compare{
    line-height: 1.7;
}

/*COR BOTÃO COMPRAR*/
.btn-primary {
    color: #fff;
    background-color: green;
    border-color: transparent;
    text-transform: uppercase;
}

/*ELIMINAR PREÇO PARCELADO*/
.prices:not(.prices--big) .prices__installments {
    display: none;
}

.product__free-shipping-from {
    display: none;
}

@media screen (max-width: 768px){
  body{
    font-family: 'Aller', 'Noto Sans JP', sans-serif;
  }

  /*TAMANHO FONTE TARJA CABEÇALHO MOBILE*/
  .top-bar__countdown {
    font-size: 12px;
  }

  /*TAMANHO FONTE PREÇO BOLETO MOBILE*/
  .prices__discount{
    font-size: 13px;
  }

  /*REMOÇÃO DO FRETE GRÁTIS ABAIXO DA TABELA DE FRETE*/
  .product__free-shipping-from {
    display: none;
  }

}
